mapper.select:
SELECT b.BillId, b.UserId, b.BillType, b.BillMoney, b.ToUserId, b.CreateTime, b.BillState, b.OrderId, u.NickName userNickName, u.UserPic userPic, us.NickName toUserNickName, us.UserPic toUserPic, o.ServiceId, o.BuyerId, o.IsAllowChanged, o.RecAddrId, o.OrderState, o.Commission, o.ManYunTip, o.Prepay, o.Remark, o.CreateTime oCreateTime, o.UpdateTime, o.RefuseReasonId, o.Score, o.Evaluate FROM bill b LEFT JOIN ks_user u ON b.UserId = u.UserId LEFT JOIN ks_user us ON b.ToUserId = us.UserId LEFT JOIN orders o ON b.OrderId = o.OrderId WHERE b.UserId = #{userId} or b.ToUserId = #{userId}
and b.BillState = #{billState}
ORDER BY b.BillId DESC LIMIT #{pageNum},#{pageSize}

SELECT b.BillId, b.UserId, b.BillType, b.BillMoney, b.ToUserId, b.CreateTime, b.BillState, b.OrderId, u.NickName nickName, u.UserPic userPic, u.PhoneNum phoneNum, u.OpenId userOpenId, us.NickName toNickName, us.UserPic toUserPic, us.PhoneNum toPhoneNum, us.OpenId toUserOpenId, o.ServiceId, o.BuyerId, o.IsAllowChanged, o.RecAddrId, o.OrderState, o.Commission, o.Prepay, o.Remark, o.CreateTime oCreateTime, o.UpdateTime, o.RefuseReasonId, o.Score, o.Evaluate, p.PlaceName FROM bill b LEFT JOIN ks_user u ON b.UserId = u.UserId LEFT JOIN ks_user us ON b.ToUserId = us.UserId LEFT JOIN orders o ON b.OrderId = o.OrderId LEFT JOIN receive_address ra ON o.RecAddrId = ra.Id LEFT JOIN place p ON ra.PlaceId= p.PlaceId where 1 = 1
and b.OrderId = #{orderId}
and b.BillId = #{billId}
and b.BillState = #{billState}
and b.BillType = #{billType}

SELECT b.BillId billId, b.UserId userId, b.BillType billType, b.BillMoney billMoney, b.ToUserId toUserId, b.CreateTime createTime, b.BillState billState, b.OrderId orderId FROM bill b where 1 = 1
and b.UserId = #{userId}
and b.BillState = #{billState}
and b.BillType = #{billType}

SELECT a.BillId, a.UserId, a.BillType, a.BillMoney, a.ToUserId, a.CreateTime, a.BillState, a.OrderId FROM bill a WHERE 1 = 1
and a.BillType = #{billType}
and a.OrderId = #{orderId}
